//import React from 'react'
import './home.scss';
import { useState } from 'react';

export default function Home() {
    const [currentSlide,setCurrentSlide] = useState(0);
    const data=[
        {
            id:"1",
            icon:"assets/mobile.png",
            title:"Portfolio",
            desc:"This Portfolio was made using ReactJs and SCSS. It has 3 pages namely About, Projects and Contacts.",
            img:"assets/react.png",
        },
        {
            id:"2",
            icon:"assets/mobile.png",
            title:"Web Development",
            desc:"Tokyo, officially the Tokyo Metropolis, is the capital and most populous prefecture of Japan. Located at the head of Tokyo Bay, the prefecture forms part of the Kantō region on the central Pacific .",
            img:"assets/web.jpg",
        },
        {
            id:"3",
            icon:"assets/mobile.png",
            title:"Python Project",
            desc:"Tokyo, officially the Tokyo Metropolis, is the capital and most populous prefecture of Japan. Located at the head of Tokyo Bay, the prefecture forms part of the Kantō region on the central Pacific .",
            img:"assets/web.jpg",
        },
        
    ]
    const handleClick = (way)=>{
        way === "left" ? setCurrentSlide(currentSlide>0? currentSlide-1 : 2 ) : 
        setCurrentSlide(currentSlide<data.length-1 ? currentSlide+1 :0);
    }

    return (
        <div className="home" id="home">
            <div className="slider" style={{transform: `translateX(-${currentSlide * 100}vw)` }}>
            {data.map((d)=>(

                <div className="container">
                    <div className="item">
                        <div className="left">
                            <div className= "leftContainer">    
                                <div className="imgContainer">
                                    <img src={d.icon} alt=""/>
                                </div>
                                <h2>{d.title}</h2>
                                <p>
                                    {d.desc}
                                </p>
                                <span>
                                    Projects
                                </span>
                            </div>

                        </div>
                        <div className="right">
                            <img src={d.img} alt=""/>
                        </div>
                    </div>
                </div>
            ))}
            </div>
            <img src="assets/down.png" className="arrow left" alt="" onClick={()=>handleClick("left")}/>
            <img src="assets/down.png" className="arrow right" alt="" onClick={()=>handleClick("right")} />
        </div>
    )
}
