//import React from 'react'
import './home.scss';
import { useState } from 'react';

export default function Home() {
    const [currentSlide,setCurrentSlide] = useState(0);
    const data=[
        {
            id:"1",
            icon:"assets/mobile.png",
            title:"Portfolio",
            desc:"This Portfolio was made using ReactJs and SCSS. It has 3 pages namely About, Projects and Contacts.",
            img:"assets/rart.jpg",
        },
        {
            id:"2",
            icon:"assets/mobile.png",
            title:"Result Analyzer",
            desc:"This python project fetches result from official website of KNIT Sultanpur and displays it in Rnakwise order. It also gives you option to display complete latest result of a particular student.",
            img:"assets/pozn.jpg",
        },
        {
            id:"3",
            icon:"assets/mobile.png",
            title:"Wiki Path Finder",
            desc:"This Project gives you shortest Path between two wikipedia pages. It was created using python and it gives path using wikipedia dumps ",
            img:"assets/pyt.jpg",
        },
        {
            id:"4",
            icon:"assets/mobile.png",
            title:"Dummy Website",
            desc:"This is a Dummy website of KNIT Sultanpur created using HTML, CSS and Vanilla JS. It was created in a span of 16 hours and contains all the major pages from the original website knit.ac.in",
            img:"assets/web.jpg",
        },
        
    ]
    const handleClick = (way)=>{
        way === "left" ? setCurrentSlide(currentSlide>0? currentSlide-1 : 3 ) : 
        setCurrentSlide(currentSlide<data.length-1 ? currentSlide+1 :0);
    }

    return (
        <div className="home" id="home">
            <div className="slider" style={{transform: `translateX(-${currentSlide * 100}vw)` }}>
            {data.map((d)=>(

                <div className="container">
                    <div className="item">
                        <div className="left">
                            <div className= "leftContainer">    
                                <div className="imgContainer">
                                    <img src={d.icon} alt=""/>
                                </div>
                                <h2>{d.title}</h2>
                                <p>
                                    {d.desc}
                                </p>
                                <span>
                                    Projects
                                </span>
                            </div>

                        </div>
                        <div className="right">
                            <img src={d.img} alt=""/>
                        </div>
                    </div>
                </div>
            ))}
            </div>
            <img src="assets/down.png" className="arrow left" alt="" onClick={()=>handleClick("left")}/>
            <img src="assets/down.png" className="arrow right" alt="" onClick={()=>handleClick("right")} />
        </div>
    )
}
